	constructor(name) {
		this.name = name;
	}
}

class Labrador extends Dog {
	// 1
	constructor(name, size) {
		this.size = size;
	}
	// 2
	constructor(name, size) {
		super(name);
		this.size = size;
	}
	// 3
	constructor(size) {
		super(name);
		this.size = size;
	}
